
const themeButton = document.getElementById('js-mode-btn');
themeButton.addEventListener('change',() => {

  if(themeButton.checked == true){
    document.body.classList.add('darkTheme');
  } else {
    document.body.classList.remove('darkTheme');
  }
});

localStorage.setItem('theme',themeButton.checked);

const burgerButton = document.getElementById('burger-button');
const sidebarMenu = document.getElementById('sidebarMenu');
const contentWrapper = document.getElementById('contentWrapper');
const windowWidth = window.innerWidth;
const windowPc = 1024
burgerButton.addEventListener('click', () => {
  burgerButton.classList.toggle('deleteClass');

  if(sidebarMenu.classList.contains('is-show')){
    sidebarMenu.classList.remove('is-show');
    contentWrapper.classList.add('container-fluid');
  }else{
    sidebarMenu.classList.add('is-show');
    contentWrapper.classList.remove('container-fluid');
  }

});

const displayMode = document.getElementById('displayMode');
displayMode.addEventListener('click', () => {
  if(displayMode.innerHTML === '通常'){
    displayMode.innerHTML = '超低遅延';
  }else {
    displayMode.innerHTML = '通常';
  }
});

const displayScale = document.getElementById('displayScale');
displayScale.addEventListener('click', () => {
  if(displayScale.innerHTML === '日'){
    displayScale.innerHTML = '時';
  }else if(displayScale.innerHTML === '時'){
    displayScale.innerHTML = '分';
  }else {
    displayScale.innerHTML = '日';
  }
});

const rangeSlider = document.getElementById("rs-range-line");
const rangeBullet = document.getElementById("rs-bullet");
rangeSlider.addEventListener("input", showSliderValue, false);
function showSliderValue() {
  rangeBullet.innerHTML = rangeSlider.value;
  const bulletPosition = (rangeSlider.value /rangeSlider.max);
  rangeBullet.style.bottom = (bulletPosition * 418) + "px";
}


